// src/Components/ProductPage/ProductPage.jsx
import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import products from '../../data/products'; // Import products data
import './ProductPage.css'; // Styling for the page

const ProductPage = () => {
  const { productId } = useParams();
  const product = products.find((item) => item.id === parseInt(productId));
  const [quantity, setQuantity] = useState(1);

  if (!product) {
    return <p>Product not found.</p>;
  }

  const handleAddToCart = () => {
    alert(`Added ${quantity} of ${product.name} to the cart.`);
    // Additional logic for adding to cart can be added here
  };

  return (
    <div className="product-page">
      <div className="product-container">
        <div className="product-image-section">
          <img src={product.image} alt={product.name} />
          <button className="top-seller">Top Seller</button>
        </div>
        <div className="product-details-section">
          <h1 className="product-name">{product.name}</h1>
          <p className="product-code">Product Code: {product.serialNumber || "123456789"}</p>
          <div className="rating">
            <span className="stars">★★★★☆</span>
            <span className="review-count">(1 Review)</span>
          </div>
          <div className="price-section">
            <span className="original-price">{product.originalPrice || "799 TL"}</span>
            <span className="discounted-price">{product.price}</span>
            <span className="discount-rate">6% OFF</span>
          </div>
          <button className="add-to-cart-button" onClick={handleAddToCart}>
            Add to Cart
          </button>
          <div className="additional-info">
            <p><strong>Distributor:</strong> {product.distributor || "FITFIYATHOME"}</p>
            <p><strong>Shipping:</strong> 1-2 business days</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductPage;
