// ProductPage.jsx
import React from 'react';
import { useParams } from 'react-router-dom';
import products from '../../data/products'; // Adjust the path based on where your products data is stored
import './ProductPage.css'; // Optional: create a CSS file for styling

const ProductPage = () => {
  const { productId } = useParams(); // Get the product ID from the route parameters
  const product = products.find((item) => item.id === parseInt(productId)); // Find the product by ID

  if (!product) {
    return <p>Product not found.</p>;
  }

  return (
    <div className="product-page">
      <img src={product.image} alt={product.name} />
      <h1>{product.name}</h1>
      <p>{product.description}</p>
      <p><strong>Price:</strong> {product.price}</p>
    </div>
  );
};

export default ProductPage;
