// src/Components/ProductPage/ProductPage.jsx
import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import products from '../../data/products'; // Adjust path to where products are stored
import './ProductPage.css';

const ProductPage = () => {
  const { productId } = useParams();
  const product = products.find((item) => item.id === parseInt(productId));

  const [quantity, setQuantity] = useState(1);
  const [comment, setComment] = useState("");
  const [rating, setRating] = useState(0);

  if (!product) {
    return <p>Product not found.</p>;
  }

  const handleAddToCart = () => {
    alert(`Added ${quantity} of ${product.name} to the cart.`);
    // Additional logic to handle cart functionality
  };

  const handleCommentSubmit = () => {
    alert(`Comment submitted: "${comment}" with rating ${rating}/5.`);
    // Additional logic to save comment and rating
    setComment("");
    setRating(0);
  };

  return (
    <div className="product-page">
      <img src={product.image} alt={product.name} />
      <h1>{product.name}</h1>
      <p><strong>Model:</strong> {product.model || "Model information"}</p>
      <p><strong>Serial Number:</strong> {product.serialNumber || "Serial number info"}</p>
      <p><strong>Description:</strong> {product.description}</p>
      <p><strong>Price:</strong> {product.price}</p>
      <p><strong>Warranty Status:</strong> {product.warranty || "1 Year"}</p>
      <p><strong>Distributor:</strong> {product.distributor || "Distributor information"}</p>
      <p><strong>Stock:</strong> {product.stock || "Out of Stock"}</p>

      <div className="cart-options">
        <label>
          Quantity:
          <input
            type="number"
            min="1"
            max={product.stock || 1}
            value={quantity}
            onChange={(e) => setQuantity(parseInt(e.target.value))}
          />
        </label>
        <button onClick={handleAddToCart} disabled={!product.stock}>Add to Cart</button>
      </div>

      <div className="product-reviews">
        <h2>Leave a Review</h2>
        <label>
          Rating:
          <input
            type="number"
            min="1"
            max="5"
            value={rating}
            onChange={(e) => setRating(parseInt(e.target.value))}
          />
        </label>
        <textarea
          value={comment}
          onChange={(e) => setComment(e.target.value)}
          placeholder="Write your comment here..."
        />
        <button onClick={handleCommentSubmit}>Submit Comment</button>
      </div>
    </div>
  );
};

export default ProductPage;
